    return grid[row].indexOf(num)=== -1
  }
  function checkCol( grid, col, num){
    return grid.map(row => row[col].indexOf(num)=== -1)
  }
  function checkBox(grid,row,col,num){
    let boxArr =[]
    rowStart = row- (row%3);
    colStart = col -(col%3);
    for(let i=0; i< 3;i++){
      for(let j=0; j<3;j++){
        boxArr.push(grid[rowStart + i][colStart + i])
      }
    }
    return boxArr .indexOf(num)===-1
  }
  function checkValid(grid,row,num,col ){
     if(checkRow(grid, row, num) && checkCol(grid, col, num) && checkBox(grid,col,row,num)){
      return true
     }
     return false
  }
  function solver(grid,row=0,col= 0){
    for(let num = 1; num <=9; num++){
      if(checkValid(grid,row,col,num)){
        grid[row][col]= num
      }
    }
    grid[row][c]
  }
  function solveSudoku(){
    let sudoku = getDeepCopy(initial);
    solver(sudoku);
    setSudokuArr(sudoku); 
  }

  return (
    <div className="app">
      <div className="App-header">
        <h3>Sudoku solver</h3>

        <table>
          <tbody>
            {
              [0, 1, 2, 3, 4, 5, 6, 7, 8].map((row, rIndex) => {
                return <tr key={rIndex} className={(row + 1) %3 === 0 ? 'bBorder' : ''}>
                  {[0, 1, 2, 3, 4, 5, 6, 7, 8].map((col, cIndex) => {
                    return <td key={rIndex + cIndex} className={(col + 1) %3 === 0 ? 'rBorder' : ''} >
                      <input onChange={(e)=> onInputChange(e,row,col)} 
                        value={sudokuArr[row][col]=== -1 ? '' : sudokuArr[row][col] } 
                        className="cellInput"  
                        disabled={initial[row][col] !== -1}/>
                    </td>
                  })}
                </tr>

              })
            }
          </tbody>
        </table>
        <div className='buttonContainer'>
          <button className='validateButton' onClick="validateSudoku">Validate</button>
          <button className='solveButton' onClick="solveSudoku">Solve</button>

        </div>
      </div>
    </div>
  )

}

export default App;
